mapper.update:
update admin_user

    password = #{password},
    enabled = #{enabled}

where id = #{userId};

update user_profile

    email = #{email},
    qq = #{qq},
    city = #{city},
    sex = #{sex}

where user_id = #{userId};

delete from user_category where user_id = #{userId};
delete from user_forum where user_id = #{userId};
delete from admin_user__role where user_id = #{userId};

    insert into admin_user__role(user_id, role_id) values
     (#{userId}, #{roleId})

delete from user_category where user_id = #{userId};
delete from user_forum where user_id = #{userId};

    insert into user_category(user_id, category_id) values
     (#{userId}, #{categoryId})

delete from user_category where user_id = #{userId};
delete from user_forum where user_id = #{userId};

    insert into user_forum(user_id, forum_id) values
     (#{userId}, #{forumId})

mapper.delete:
delete from admin_user where id in #{item};
delete from admin_user__role where user_id in #{item};
delete from user_profile where user_id in #{item};
delete from user_category where user_id in #{item};
delete from user_forum where user_id in #{item};
# 不删除主题和回复

mapper.select:
select f.id, f.name, f.status , uf.id selected from forum f
left join user_forum uf on uf.user_id = #{userId} and uf.forum_id = f.id
left join category_forum cf on cf.forum_id = f.id left join user_category uc on uc.category_id = cf.category_id where uc.user_id = ${operatorUserId}
