mapper.select:
SELECT count(*)
FROM admin_role
WHERE name = #{name}

SELECT r1.id, r1.name, r1.description, r1.enabled, r1.gmt_create, r1.gmt_modify FROM admin_role r1 INNER JOIN ( SELECT id FROM admin_role ORDER BY ${sort}
${order}
LIMIT #{start}, #{pageSize}) r2 ON r1.id = r2.id

mapper.insert:
DELETE FROM admin_user_role WHERE user_id = #{userId};
INSERT INTO admin_user_role(user_id, role_id, gmt_create, gmt_modify) VALUES
(#{userId}, #{item}, current_timestamp, current_timestamp)
